import React , { Component } from 'react';
import CardList from '../components/card_list';
import SearchBox from '../components/search_box';
import Scroll from '../components/Scroll';
import { connect } from 'react-redux';


import { setSearchField , requestRobots } from '../actions'


const mapStateToProps = (state) => {
	return {
		searchField: state.searchRobots.searchField,
		robots : state.requestRobots.robots,
		isPedding : state.requestRobots.isPedding,
		error : state.requestRobots.eror
	}
}

const mapDispatchToProps = (dispatch) =>{
	return {
		onSearchChange : (event) => dispatch(setSearchField(event.target.value)),
		onRequestRobots: () => dispatch(requestRobots())
	}
}


class App extends Component  {
	constructor(){
		super()
		this.state={
			robots : [] ,
		}

	}
    componentDidMount(){
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response =>response.json())
		.then(users => this.setState({robots : users}));
	}
	render() {
		const{ robots } = this.state;
		const { searchField , onSearchChange } = this.props;
		const filteredRobots = robots.filter(robot =>{
			return robots.name.toLowerCase().includes(searchField.toLowerCase())
		})
		if(this.state.robots.length === 0){
			return <h1> Loading </h1>
		}else {
			return (
				<div className = "tc">
					<h1 className="b dim  f1 f2-m f1-l fw2 black-90 mv3"> RoboFriends</h1>
					<h2 className="f5 f4-m f3-l fw2 black-50 mt0 lh-copy"> This is my first <em>React </em> app</h2>
					<SearchBox searchChange={onSearchChange}/>
					<Scroll>
						<CardList robots={filteredRobots} />
					</Scroll>
				</div>
			);
		}
	}
}

export default connect(mapStateToProps , mapDispatchToProps)(App);
